import React, { useEffect } from "react";
import { useGlobalContext } from "../../context/globalContext";
import styled from "styled-components";

const Transactions = () => {
  const { getIncomes, incomes, getExpenses, expenses, error } = useGlobalContext();

  useEffect(() => {
    // Fetch incomes and expenses when the component mounts
    getIncomes();
    getExpenses();
  }, []);


  return (
    <TransactionStyled>
      <h2>Transactions</h2>

      <div className="content">
        <div className="card">
            <h3>Incomes</h3>
            <div className="inner-content">
                {incomes.map((income) => (
                    <p key={income._id}>
                    {income.title} - ${income.amount}
                    </p>
                ))}
            </div>
        </div>

        <div>
            <h3>Expenses</h3>
            <div className="inner-content">
                {expenses.map((expense) => (
                    <p key={expense._id}>
                    {expense.title} - ${expense.amount}
                    </p>
                ))}
            </div>
        </div>
      </div>
      {/* {incomes.map((income) => {
            const {_id, title, amount, date, category, description, type} = income;
            <p>{title}</p>


        })} */}
    </TransactionStyled>
  );
};

const TransactionStyled = styled.div`
    display: flex;
    flex-direction: column;
    align-items: flex-start;
    overflow: auto;
    gap: .2rem;
    padding: 2rem;

    .content{
        flex: 1;
        display: flex;
        flex-direction: column;
        gap: .2rem;
        h5{
            font-size: 1.3rem;
            padding-left: 2rem;
            position: relative;
            &::before{
                content: '';
                position: absolute;
                left: 0;
                top: 50%;
                transform: translateY(-50%);
                width: .8rem;
                height: .8rem;
                border-radius: 50%;
                background-color: ${props => props.indicator};
            }
        }
        .card{
            background-color: red;
        }

        .inner-content{
            display: flex;
            flex-direction: column;
            justify-content: space-between;
            .text{
                display: flex;
                flex-direction: column;
                align-items: center;
                gap: 1.5rem;
                color: var(--primary-color);
                p{
                    display: flex;
                    flex-direction: column;
                    align-items: center;
                    gap: 0.5rem;
                    color: var(--primary-color);
                    opacity: 0.8;
                    font-size: 14px;
                }
            }
        }

`;

export default Transactions;
